import React, { Component } from "react";
import { Table, Button, Label } from "semantic-ui-react";
import web3 from "../ethereum/web3";
import { Link } from "react-router";

// show request pages that will show rows of all the requests available in the contract
class RequestRowsCompany extends Component {
  render() {
    // js syntax to destructure variables and objects.
    const { Row, Cell } = Table;
    const { id, request } = this.props;
    var approve;
    if (request[2] == 1) {
      approve = "Requested";
    } else if (request[2] == 2) {
      approve = "Approved";
    } else {
      approve = "Rejected";
    }

    return (
      <Row>
        <Cell>
          <Label color="black" ribbon>
            {id + 1}
          </Label>
        </Cell>
        <Cell>{request[0]}</Cell>
        <Cell>{approve}</Cell>
        <Cell>
          <Link>
            <Button basic color="blue" content="blue">
              View Document
            </Button>
          </Link>
        </Cell>
      </Row>
    );
  }
}

export default RequestRowsCompany;
