      Array(parseInt(requestCount))
        .fill()
        .map((element, index) => {
          return campaign.methods.requests(index).call();
        })
    );
  };

  render() {
    const { Header, Row, HeaderCell, Body } = Table;

    return (
      <Layout>
        <Button onClick={this.renderRows}>click</Button>
        <Table>
          <Header>
            <Row>
              <HeaderCell>User Address</HeaderCell>
              <HeaderCell>Approval Status</HeaderCell>
              <HeaderCell>More info</HeaderCell>
            </Row>
          </Header>
          <Body></Body>
        </Table>
      </Layout>
    );
  }
}

export default ViewRequest;
