import React, { Component } from "react";
import web3 from "../../ethereum/web3";
import instance from "../../ethereum/compiledID";
import Layout from "../../components/Layout";
import {
  Form,
  Header,
  Grid,
  Button,
  Segment,
  Message,
} from "semantic-ui-react";

class NewRequest extends Component {
  render() {
    return (
      <Layout>
        <Header as="h3" content="Create new wallet" />

        <Grid>
          <Grid.Column width={8}>
            <Message
              attached
              //   style={{ width: "60%" }}
              header="Welcome To Create Wallet Page!"
              content="Fill out the form below to Register"
            />
            <Form
              error={!!this.state.errMessage}
              onSubmit={this.onSubmit}
              className="attached segment"
            >
              <Form.Field>
                <Form.Input
                  error={
                    !this.state.errorName
                      ? null
                      : {
                          content: this.state.errorName,
                          pointing: "above",
                        }
                  }
                  label="Full name"
                  onKeyUp={this.validation}
                  type="text"
                  value={this.state.name}
                  onChange={(event) => {
                    this.setState({ name: event.target.value });
                  }}
                  placeholder="Enter full name"
                />
                <Form.Input
                  error={
                    !this.state.errorEmail
                      ? null
                      : {
                          content: this.state.errorEmail,
                          pointing: "above",
                        }
                  }
                  label="Email"
                  type="text"
                  onKeyUp={this.validation}
                  value={this.state.email}
                  onChange={(event) => {
                    this.setState({ email: event.target.value });
                  }}
                  placeholder="Enter email ID"
                />
                <Form.Input
                  error={
                    !this.state.errorNumber
                      ? null
                      : {
                          content: this.state.errorNumber,
                          pointing: "above",
                        }
                  }
                  label="Mobile number"
                  value={this.state.number}
                  onKeyUp={this.validation}
                  onChange={(event) => {
                    this.setState({ number: event.target.value });
                  }}
                  placeholder="Enter Mobile Number"
                />
              </Form.Field>
              <Message
                error
                header="There was some errors with your submission"
                content={this.state.errMessage}
              />

              <Button
                loading={this.state.loading}
                disabled={this.state.loading}
                primary
              >
                Create Wallet
              </Button>
            </Form>
          </Grid.Column>
          <Grid.Column width={8}>
            <Segment
              style={{
                backgroundColor: "#6886c5",
                height: "100%",
                textAlign: "center",
              }}
            >
              <div
                style={{
                  textAlign: "center",
                  position: "relative",
                  top: "20%",
                }}
              >
                <p
                  style={{
                    color: "white",
                    fontSize: "50px",
                    fontWeight: "bold",
                    // paddingTop: "20%",
                  }}
                >
                  Create You Wallet
                  <br />
                  Keep Your Documents Safe!
                </p>
                {/* <p
                  style={{
                    // position: "relative",
                    color: "white",
                    fontSize: "30px",
                    fontWeight: "bold",
                    // bottom: "15%",
                  }}
                ></p> */}
              </div>
            </Segment>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default NewRequest;
