import React, { Component } from "react";
import { Header, Table } from "semantic-ui-react";
import Layout from "../../components/Layout";
import web3 from "../../ethereum/web3";
import instance from "../../ethereum/compiledID";

class ViewDocument extends Component {
  static getInitialProps(props) {
    const { address } = props.query;

    return { address };
  }
  //   componentDidMount() {
  //     const doc = this.fetchdata();
  //     console.log(doc);
  //   }
  fetchdata = async () => {
    const accounts = await web3.eth.getAccounts();
    const doc = await instance.methods.viewUserLicence(accounts[0]).call();
    this.setState({ doc });
    const array = Array(5).fill(
      doc.map((value) => {
        return value;
      })
    );
    return array;
  };
  async render() {
    const { Header, Row, HeaderCell, Body, Cell } = Table;
    const array = this.fetchdata();
    return (
      <Layout>
        <Header as="h3" content="Document Info" />
        <div style={{ position: "relative", left: "25%" }}>
          <Table style={{ width: "50%" }} celled>
            <Body>
              <Row>
                <Cell>Licence Number</Cell>
                <Cell>{array[0]}</Cell>
              </Row>
              <Row>
                <Cell>Name</Cell>
                <Cell>{array[1]}</Cell>
              </Row>
              <Row>
                <Cell>DOB</Cell>
                <Cell>{array[2]}</Cell>
              </Row>
              <Row>
                <Cell>Address</Cell>
                <Cell>{array[3]}</Cell>
              </Row>
            </Body>
          </Table>
        </div>
      </Layout>
    );
  }
}

export default ViewDocument;
