import React, { Component } from "react";
import Layout from "../../components/Layout";
import {
  Form,
  Input,
  Header,
  Button,
  Message,
  Grid,
  Segment,
} from "semantic-ui-react";

class AddUser extends Component {
  state = {
    name: "",
    email: "",
    number: "",
    errorName: "",
    errorEmail: "",
    errorNumber: "",
  };
  render() {
    return (
      <Layout>
        <Header as="h3" content="Create new wallet" />

        <Grid>
          <Grid.Column width={8}>
            <Message
              attached
              //   style={{ width: "60%" }}
              header="Welcome to our site!"
              content="Fill out the form below to sign-up for a new account"
            />
            <Form className="attached segment">
              <Form.Input
                label="Full name"
                type="text"
                value={this.state.name}
                onChange={(event) => {
                  this.setState({ name: event.target.value });
                }}
                placeholder="Enter full name"
              />
              <Form.Input
                label="Email"
                type="text"
                value={this.state.email}
                onChange={(event) => {
                  this.setState({ email: event.target.value });
                }}
                placeholder="Enter email ID"
              />
              <Form.Input
                label="Mobile number"
                value={this.state.number}
                onChange={(event) => {
                  this.setState({ number: event.target.value });
                }}
                placeholder="Enter Mobile Number"
              />

              <Button primary>Create Wallet</Button>
            </Form>
          </Grid.Column>
          <Grid.Column width={8}>
            <Segment
              style={{
                backgroundColor: "#6886c5",
                height: "100%",
                textAlign: "center",
              }}
            >
              <p
                style={{
                  color: "white",
                  fontSize: "50px",
                  fontWeight: "bold",
                  paddingTop: "50%",
                }}
              >
                Create You Wallet
              </p>
              <p
                style={{
                  position: "relative",
                  color: "white",
                  fontSize: "30px",
                  fontWeight: "bold",
                  bottom: "15%",
                }}
              >
                Keep Your Documents Safe
              </p>
            </Segment>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default AddUser;
