    //       request={request}
    //       address={this.props.address}
    //       approversCount={this.props.approversCount}
    //     />
    //   );
    // });
  };

  render() {
    const { Header, Row, HeaderCell, Body } = Table;
    <Button onClick={this.renderRows}></Button>;
    return (
      <Layout>
        <Table>
          <Header>
            <Row>
              <HeaderCell>User Address</HeaderCell>
              <HeaderCell>Approval Status</HeaderCell>
              <HeaderCell>More info</HeaderCell>
            </Row>
          </Header>
          <Body></Body>
        </Table>
      </Layout>
    );
  }
}

export default ViewRequest;
