import React, { Component } from "react";
import Layout from "../../components/Layout";
import {
  Form,
  Input,
  Header,
  Button,
  Message,
  Grid,
  Segment,
} from "semantic-ui-react";

class AddUser extends Component {
  state = {
    name: "",
    email: "",
    number: "",
    errorName: "",
    errorEmail: "",
    errorNumber: "",
    loading: false,
  };

  isAlpha(input) {
    var pattern = /^[a-zA-Z ]+$/;
    return pattern.test(input);
  }

  onSubmit = (event) => {
    event.preventDefault();

    let errorName;
    let errorEmail;
    let errorNumber;
    //Required
    if (!this.state.name) {
      errorName = "Required Fields!";
      this.setState({ errorName });
    } else {
      var bool = this.isAlpha(this.state.name);
      if (!bool) {
        this.setState({ errorName: "Name field can only contain alphabets!" });
      }
    }

    if (!this.state.email) {
      errorEmail = "Required Fields!";
    }
    if (!this.state.number) {
      errorNumber = "Required Fields!";
    }
  };
  render() {
    return (
      <Layout>
        <Header as="h3" content="Create new wallet" />

        <Grid>
          <Grid.Column width={8}>
            <Message
              attached
              //   style={{ width: "60%" }}
              header="Welcome to our site!"
              content="Fill out the form below to sign-up for a new account"
            />
            <Form
              error={
                !!this.state.errorName ||
                !!this.state.errorEmail ||
                !!this.state.errorNumber
              }
              onSubmit={this.onSubmit}
              className="attached segment"
            >
              <Form.Field>
                <Form.Input
                  error={
                    !this.state.errorName
                      ? null
                      : {
                          content: this.state.errorName,
                          pointing: "above",
                        }
                  }
                  label="Full name"
                  type="text"
                  value={this.state.name}
                  onChange={(event) => {
                    this.setState({ name: event.target.value });
                  }}
                  placeholder="Enter full name"
                />
                <Form.Input
                  label="Email"
                  type="text"
                  value={this.state.email}
                  onChange={(event) => {
                    this.setState({ email: event.target.value });
                  }}
                  placeholder="Enter email ID"
                />
                <Form.Input
                  label="Mobile number"
                  value={this.state.number}
                  onChange={(event) => {
                    this.setState({ number: event.target.value });
                  }}
                  placeholder="Enter Mobile Number"
                />
              </Form.Field>
              <Message
                error
                header="There was some errors with your submission"
                list={[
                  this.state.errorName,
                  this.state.errorEmail,
                  this.state.errorNumber,
                ]}
              />

              <Button
                loading={this.state.loading}
                disabled={this.state.loading}
                primary
              >
                Create Wallet
              </Button>
            </Form>
          </Grid.Column>
          <Grid.Column width={8}>
            <Segment
              style={{
                backgroundColor: "#6886c5",
                height: "100%",
                textAlign: "center",
              }}
            >
              <div
                style={{
                  textAlign: "center",
                  position: "relative",
                  top: "20%",
                }}
              >
                <p
                  style={{
                    color: "white",
                    fontSize: "50px",
                    fontWeight: "bold",
                    // paddingTop: "20%",
                  }}
                >
                  Create You Wallet
                  <br />
                  Keep Your Documents Safe
                </p>
                {/* <p
                  style={{
                    // position: "relative",
                    color: "white",
                    fontSize: "30px",
                    fontWeight: "bold",
                    // bottom: "15%",
                  }}
                ></p> */}
              </div>
            </Segment>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default AddUser;
