import React, { Component } from "react";
import web3 from "../../ethereum/web3";
import instanceID from "../../ethereum/compiledID";
import Layout from "../../components/Layout";
import { Table } from "semantic-ui-react";
import RequestRows from "../../components/RequestRows";

class ViewRequest extends Component {
  static async getInitialProps(props) {}
  state = {
    requests: undefined,
  };

  async componentDidMount() {
    const accounts = await web3.eth.getAccounts();
    const count = await instanceID.methods
      .viewRequestLength(accounts[0])
      .call();

    const requests = await Promise.all(
      Array(parseInt(count))
        .fill()
        .map((element, index) => {
          return instanceID.methods
            .viewRequestHeader(accounts[0], index)
            .call();
        })
    );
    this.setState({ requests });
  }

  // renderRows = async () => {
  //   return this.state.requests.map((request, index) => {
  //     // return <RequestRows key={index} id={index} request={request} />;
  //     console.log(request);
  //   });
  // };

  render() {
    const { Header, Row, HeaderCell, Body, Cell } = Table;
    this.renderRows();
    return (
      <Layout>
        <Table>
          <Header>
            <Row>
              <HeaderCell>ID</HeaderCell>
              <HeaderCell>User Address</HeaderCell>
              <HeaderCell>Approval Status</HeaderCell>
              <HeaderCell>More info</HeaderCell>
            </Row>
          </Header>
          <Body>
            <Row>
              <Cell></Cell>
            </Row>
          </Body>
        </Table>
      </Layout>
    );
  }
}

export default ViewRequest;
