import React, { Component } from "react";
import Layout from "../../components/Layout";
import instanceID from "../../ethereum/compiledID";
import {
  Form,
  Input,
  Header,
  Button,
  Message,
  Grid,
  Segment,
} from "semantic-ui-react";
import Web3 from "../../ethereum/web3";

class AddUser extends Component {
  state = {
    name: "",
    email: "",
    number: "",
    errorName: "",
    errorEmail: "",
    errorNumber: "",
    loading: false,
    errMessage: "",
  };

  isAlpha(input) {
    var pattern = /^[a-zA-Z ]+$/;
    return pattern.test(input);
  }

  isEmail(input) {
    var pattern = /^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/;
    return pattern.test(input);
  }

  isMobileNumber(input) {
    var pattern = /^([0-9]{10})$/;
    return pattern.test(input);
  }

  onSubmit = async (event) => {
    event.preventDefault();

    this.setState({ errorName: "", errorEmail: "", errorNumber: "" });

    let errorName;
    let errorEmail;
    let errorNumber;
    //Required

    // For name field!
    if (!this.state.name) {
      errorName = "Required Fields!";
      this.setState({ errorName });
    } else {
      var bool = this.isMobileNumber(this.state.number);
      if (!bool) {
        this.setState({ errorNumber: "Enter a valid mobile number!" });
      }
    }

    // For Email Field
    if (!this.state.email) {
      errorEmail = "Required Fields!";
      this.setState({ errorEmail });
    } else {
      var bool = this.isEmail(this.state.email);
      if (!bool) {
        this.setState({ errorEmail: "Enter a valid email id!" });
      }
    }

    // For Number field
    if (!this.state.number) {
      errorNumber = "Required Fields!";
      this.setState({ errorNumber });
    } else {
      var bool = this.isEmail(this.state.email);
      if (!bool) {
        this.setState({ errorEmail: "Enter a valid email id!" });
      }
    }

    this.setState({ loading: true, errMessage: "" });
    const accounts = await web3.eth.getAccounts();
    try {
      const { name, email, number } = this.state;
      await instanceID.methods.addUser(name, email, number).send({
        from: accounts[0],
      });
    } catch (err) {}
    await camp;
  };
  render() {
    return (
      <Layout>
        <Header as="h3" content="Create new wallet" />

        <Grid>
          <Grid.Column width={8}>
            <Message
              attached
              //   style={{ width: "60%" }}
              header="Welcome to our site!"
              content="Fill out the form below to sign-up for a new account"
            />
            <Form
              error={!!this.state.errMessage}
              onSubmit={this.onSubmit}
              className="attached segment"
            >
              <Form.Field>
                <Form.Input
                  error={
                    !this.state.errorName
                      ? null
                      : {
                          content: this.state.errorName,
                          pointing: "above",
                        }
                  }
                  label="Full name"
                  type="text"
                  value={this.state.name}
                  onChange={(event) => {
                    this.setState({ name: event.target.value });
                  }}
                  placeholder="Enter full name"
                />
                <Form.Input
                  error={
                    !this.state.errorEmail
                      ? null
                      : {
                          content: this.state.errorEmail,
                          pointing: "above",
                        }
                  }
                  label="Email"
                  type="text"
                  value={this.state.email}
                  onKeyUp={console.log("asdasd")}
                  onChange={(event) => {
                    this.setState({ email: event.target.value });
                  }}
                  placeholder="Enter email ID"
                />
                <Form.Input
                  error={
                    !this.state.errorNumber
                      ? null
                      : {
                          content: this.state.errorNumber,
                          pointing: "above",
                        }
                  }
                  label="Mobile number"
                  value={this.state.number}
                  onChange={(event) => {
                    this.setState({ number: event.target.value });
                  }}
                  placeholder="Enter Mobile Number"
                />
              </Form.Field>
              {/* <Message
                error
                header="There was some errors with your submission"
                list={[
                  this.state.errorName,
                  this.state.errorEmail,
                  this.state.errorNumber,
                ]}
              /> */}

              <Button
                loading={this.state.loading}
                disabled={this.state.loading}
                primary
              >
                Create Wallet
              </Button>
            </Form>
          </Grid.Column>
          <Grid.Column width={8}>
            <Segment
              style={{
                backgroundColor: "#6886c5",
                height: "100%",
                textAlign: "center",
              }}
            >
              <div
                style={{
                  textAlign: "center",
                  position: "relative",
                  top: "20%",
                }}
              >
                <p
                  style={{
                    color: "white",
                    fontSize: "50px",
                    fontWeight: "bold",
                    // paddingTop: "20%",
                  }}
                >
                  Create You Wallet
                  <br />
                  Keep Your Documents Safe!
                </p>
                {/* <p
                  style={{
                    // position: "relative",
                    color: "white",
                    fontSize: "30px",
                    fontWeight: "bold",
                    // bottom: "15%",
                  }}
                ></p> */}
              </div>
            </Segment>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default AddUser;
