import React, { Component } from "react";
import web3 from "../../ethereum/web3";
import instanceID from "../../ethereum/compiledID";
import Layout from "../../components/Layout";
import {
  Table,
  Header,
  Grid,
  Button,
  Segment,
  Message,
  Tab,
} from "semantic-ui-react";

class ViewRequest extends Component {
  static async getInitialProps(props) {
    const accounts = await web3.eth.getAccounts();
    console.log(accounts);

    const count = instanceID.methods.viewRequestLength(accounts[0]).call();
    console.log(count);

    const requests = await Promise.all(
      Array(parseInt(count))
        .fill()
        .map((element, index) => {
          return instanceID.methods
            .viewRequestHeader(accounts[0], index)
            .call();
        })
    );
    return { accounts, count, requests };
  }

  renderRows = () => {
    // console.log(this.state.requests.length);
    // return this.props.requests.map((request, index) => {
    //   return (
    //     <RequestRows
    //       key={index}
    //       id={index}
    //       request={request}
    //       address={this.props.address}
    //       approversCount={this.props.approversCount}
    //     />
    //   );
    // });
    console.log(this.props.accounts);
  };

  render() {
    const { Header, Row, HeaderCell, Body } = Table;

    return (
      <Layout>
        <Button onClick={this.renderRows}>click</Button>
        <Table>
          <Header>
            <Row>
              <HeaderCell>User Address</HeaderCell>
              <HeaderCell>Approval Status</HeaderCell>
              <HeaderCell>More info</HeaderCell>
            </Row>
          </Header>
          <Body></Body>
        </Table>
      </Layout>
    );
  }
}

export default ViewRequest;
