import React, { Component } from "react";
import web3 from "../../ethereum/web3";
import Layout from "../../components/Layout";
import {
  Header,
  Grid,
  Form,
  Message,
  Button,
  Segment,
} from "semantic-ui-react";

class AddLicence extends Component {
  state = {
    errMessage: "",
  };
  render() {
    return (
      <Layout>
        <Header as="h3" content="Add Your Licence!" />

        <Grid>
          <Grid.Column width={8}>
            <Message
              attached
              //   style={{ width: "60%" }}
              header="Welcome To Add Licence Page!"
              content="Fill out the form below to add your document in your wallet. 
              Remember to use the same account in metamask which you have a wallet with"
            />
            <Form
              error={!!this.state.errMessage}
              onSubmit={this.onSubmit}
              className="attached segment"
            >
              <Form.Field>
                <Form.Input
                  error={
                    !this.state.errorName
                      ? null
                      : {
                          content: this.state.errorName,
                          pointing: "above",
                        }
                  }
                  label="Licence Number"
                  onKeyUp={this.validation}
                  type="text"
                  value={this.state.name}
                  onChange={(event) => {
                    this.setState({ name: event.target.value });
                  }}
                  placeholder="Enter Licence Number"
                />
                <Form.Input
                  error={
                    !this.state.errorEmail
                      ? null
                      : {
                          content: this.state.errorEmail,
                          pointing: "above",
                        }
                  }
                  label="Name as per Licence"
                  type="text"
                  onKeyUp={this.validation}
                  value={this.state.email}
                  onChange={(event) => {
                    this.setState({ email: event.target.value });
                  }}
                  placeholder="Enter name as per licence"
                />
                <Form.Input
                  error={
                    !this.state.errorNumber
                      ? null
                      : {
                          content: this.state.errorNumber,
                          pointing: "above",
                        }
                  }
                  label="Mobile number"
                  value={this.state.number}
                  onKeyUp={this.validation}
                  onChange={(event) => {
                    this.setState({ number: event.target.value });
                  }}
                  placeholder="Enter Mobile Number"
                />
              </Form.Field>
              <Message
                error
                header="There was some errors with your submission"
                content={this.state.errMessage}
              />

              <Button
                loading={this.state.loading}
                disabled={this.state.loading}
                primary
              >
                Create Wallet
              </Button>
            </Form>
          </Grid.Column>
          <Grid.Column width={8}>
            <Segment
              style={{
                backgroundColor: "#6886c5",
                height: "100%",
                textAlign: "center",
              }}
            >
              <div
                style={{
                  textAlign: "center",
                  position: "relative",
                  top: "20%",
                }}
              >
                <p
                  style={{
                    color: "white",
                    fontSize: "50px",
                    fontWeight: "bold",
                    // paddingTop: "20%",
                  }}
                >
                  Create You Wallet
                  <br />
                  Keep Your Documents Safe!
                </p>
                {/* <p
              style={{
                // position: "relative",
                color: "white",
                fontSize: "30px",
                fontWeight: "bold",
                // bottom: "15%",
              }}
            ></p> */}
              </div>
            </Segment>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default AddLicence;
