import React, { Component } from "react";
import { Table, Button, Label } from "semantic-ui-react";
import web3 from "../ethereum/web3";

// show request pages that will show rows of all the requests available in the contract
class RequestRowsUser extends Component {
  state = {};

  render() {
    // js syntax to destructure variables and objects.
    const { Row, Cell } = Table;
    const { id, request } = this.props;
    var approve;
    if (request[2] == 1) {
      approve = "Requested";
    } else if (request[2] == 2) {
      approve = "Approved";
    } else {
      approve = "Rejected";
    }

    return (
      <Row>
        <Cell>
          <Label color="blue" ribbon>
            {request[1]}
          </Label>
        </Cell>

        <Cell>{approve}</Cell>
        <Cell>
          <Button basic color="blue" content="blue">
            Finalize
          </Button>
        </Cell>
      </Row>
    );
  }
}

export default RequestRowsUser;
